import React, {Component} from 'react';
import axios from 'axios';
import { ToastContainer, toast, zoom, Bounce } from "react-toastify";




export default class AddVehicle extends Component{
	
    constructor(props){
        super(props);
        this.state={	
			accommodations:[],		
            accommodationType:"",
            accommodationName:"",
            noOfRomm:Number,
            mobile:Number,
        }
    }

    handleInputChange=(e)=>{
        const{name,value}=e.target;	
        this.setState({
            ...this.state,
            [name]:value
        });
    };	

	onSubmit = (e) =>{
		e.preventDefault();
		const {accommodationType,accommodationName,noOfRomm,mobile}=this.state;
		const data ={
            accommodationType:accommodationType,
            accommodationName:accommodationName,
            noOfRomm:noOfRomm,
            mobile:mobile            
		}

    //console.log(data)
    axios.post("http://localhost:8070/accommodation/add",data).then((res)=>{
        if(res.data.success){
            toast.success("New Accommodation Added");
            this.setState(
                {
                    accommodationType:"",
                    accommodationName:"",
                    noOfRomm:Number,
                    mobile:Number,
                }
            )
        }else{
			toast.error("You have an Error in Inserting");
		}
    });
};

	render(){
		return (			
            <div className="row containerTop">
                <div className="col-2"/>
                <div className="col-8">
                    <div className="row">
                        <div className="col position-relative link">
                              <p><a href="/">Accommodation Management</a> {'>'} <a href="/Accommodation/">Accommodation</a> {'>'} Add Accommodation</p>
                        </div>                        
                    </div>
                    <div className="row">
                        <div className="col-9 position-relative">
                            <h2>Add Accommodation</h2>
                            <ToastContainer/>
                        </div>
                        <hr className="hr" style={{ height: '2px', color: '#0a90e8' }} />                        
                    </div>
                    <div className="shadowBox">
                        <div className="row">
                            <div className="col-6">
                                <form onSubmit={this.onSubmit}>
                                    <div className="form-row">
                                        <div className="form-group col" style={{marginTop:'15px'}}>
                                            <label>Accommodation type : </label>
                                            <select id="type" className="form-control" name="type" onChange={this.handleInputChange} value={this.state.accommodationType} required>
                                                <option selected>Choose type...</option>
                                                <option value="Hotel">Hotel</option>
                                                <option value="Motels">Motels</option>
                                                <option value="Gest House">Gest House</option>
                                                <option value="Apartment">Apartment</option>
                                                <option value="Luxury Lodges">Luxury Lodges</option>
                                            </select>
                                        </div>   
                                        <div className="form-group col" style={{marginTop:'15px'}}>
                                            <label>Accommodation Name : </label>
                                            <input type="text" className="form-control" placeholder="#River side" onChange={this.handleInputChange} value={this.state.accommodationName}  required/>
                                        </div>                                 
                                        <div className="form-group col" style={{marginTop:'15px'}}>
                                            <label>Number of Rooms : </label>
                                            <input type="number" className="form-control" id="noOfRooms" name="noOfRooms" placeholder="#20.." onChange={this.handleInputChange} value={this.state.noOfRomm} required/>
                                        </div>                                 
                                        <div className="form-group col" style={{marginTop:'15px'}}>
                                            <label>Telephone Number : </label>
                                            <input type="number" className="form-control" id="telNum" name="telNum" placeholder="011456XXXX" onChange={this.handleInputChange} value={this.state.mobile} required/>
                                        </div>                                 
                                    </div>
                                    <div className="row">
									<div className="form-group btndriver col-12"  style={{marginTop:'15px'}}>
										<div className="form-group col"  style={{marginTop:'15px'}}>	              		
											<button type="submit" className="btn btn-success" ><i className="far fa-save"></i>&nbsp;Save</button>&nbsp;&nbsp;
											<button type="reset" className="btn btn-primary"><i class="fas fa-eraser"></i>&nbsp;Clear</button>	              			
										</div>
										<div className="form-group col"  style={{marginTop:'15px'}}>
										<a href="/Accommodation/" className="btn btn-danger"><i className="fas fa-times"></i>&nbsp;Cancel</a>
										</div>              			
									</div>
									<div className="col-6"></div>
								</div>                                    
                                </form>
                            </div>
                            <div className="col-6"></div>
                        </div>
                    </div>
                </div>
                <div className="col-2"/>
            </div>
		)				
	}
}
