import React, {Component} from 'react';
import axios from 'axios';
import { ToastContainer, toast, zoom, Bounce } from "react-toastify";




export default class AddVehicle extends Component{
	
    constructor(props){
        super(props);
        this.state={	
			accommodations:[],		
            accommodationType:"",
            name:"",
            noOfRomm:Number,
            mobile:Number,
        }
    }

    handleInputChange=(e)=>{
        const{name,value} = e.target;	
        this.setState({
            ...this.state,
            [name]:value
        });
    };	

	onSubmit = (e) =>{
		e.preventDefault();
		const {accommodationType,name,noOfRomm,mobile}=this.state;
		const data ={
            accommodationType:accommodationType,
            name:name,
            noOfRomm:noOfRomm,
            mobile:mobile            
		}

    //console.log(data)
    axios.post("http://localhost:5001/accommodation/add",data).then((res)=>{
        if(res.data.success){
            toast.success("New Accommodation Added");
            this.setState(
                {
                    accommodationType:"",
                    name:"",
                    noOfRomm:Number,
                    mobile:Number,
                }
            )
        }else{
			toast.error("You have an Error in Inserting");
		}
    });
};

	render(){
		return (			
			<div className="container containerTop">		
				<div className="row">
					<h1 className="top"></h1>
				</div>	
                <div className="row">
					<div className="col-1"><h3><i class="fas fa-motorcycle"></i></h3></div>
                    <div className="col-8 position-relative">
                        <h2>Add Accommodation</h2>
						<ToastContainer/>
                    </div>
                    <div className="col-3 position-relative"></div>
                    <hr className="hr" style={{height:'2px' , color:'#0a90e8'}}/>                                
                </div>      				

				<div className="row">
					<div className="col-5">
						<img src="images/background.png" alt="delivery vehicle" style={{height:'350px' , width:'420px', marginTop:'100px'}}/>
					</div>
					<div className="col-7">
						<div className="shadowBox">
							<form onSubmit={this.onSubmit}>
								<div className="row">
									<div className="form-group col-4 position-relative" style={{marginTop:'15px'}}>
										<label for="type"> Accommodation Type : </label>
										<select id="type" className="form-control" name="type" onChange={this.handleInputChange} value={this.state.accommodationType} required>
											<option selected>Choose type...</option>
											<option value="Light motor cycles">Light motor cycles</option>
											<option value="Motorcycles">Motorcycles</option>
											<option value="Motor Tricycle">Motor Tricycle</option>
											<option value="Light Motor Lorry">Light Motor Lorry</option>
										</select>      
									</div>                
								</div>
                                <div className="form-row">
									<div className="form-group col-8 position-relative"  style={{marginTop:'15px'}}>
										<label for="model">Accommodation Name : </label>
										<input type="text" className="form-control" id="mName" name="modelName" placeholder="Enter model name" value={this.state.name} onChange={this.handleInputChange} required/>                        						  }										
									</div>									
                                </div>
                                <div className="form-row">
                                    <div className="form-group" style={{ marginTop: '15px' }}>
                                        <label for="owner">Number of Romms : </label>
                                        <input type="text" className="form-control" id="mName" name="modelName" placeholder="Enter model name" value={this.state.noOfRomm} onChange={this.handleInputChange} required/>                        						  }										
                                    </div>	
                                </div>							
								<div className="form-row">
									<div className="form-group col"  style={{marginTop:'15px'}}>
										<label for="owner">Mobile Number : </label>
										<input type="text" readOnly className="form-control" id="regdate" name="registerDate" value={this.state.mobile} onChange={this.handleInputChange} required/>					  
									</div>                        		
								</div>
								<div className="row">
									<div className="form-group btndriver col-12"  style={{marginTop:'15px'}}>
										<div className="form-group col"  style={{marginTop:'15px'}}>	              		
											<button type="submit" className="btn btn-success" ><i className="far fa-save"></i>&nbsp;Save</button>&nbsp;&nbsp;
											<button type="reset" className="btn btn-primary"><i class="fas fa-eraser"></i>&nbsp;Clear</button>	              			
										</div>
										<div className="form-group col"  style={{marginTop:'15px'}}>
										<a href="" className="btn btn-danger"><i className="fas fa-times"></i>&nbsp;Cancel</a>
										</div>              			
									</div>
									<div className="col-6"></div>
								</div>
							</form>
						</div> 
					</div>					
				</div> 
			</div>
		)				
	}
}
