import React, { Component } from 'react'
import axios from "axios";
import './AccStyles.css';


export default class AccommodationHome extends Component {

    constructor(props) {
        super(props);
        this.state={
            accommodations:[]
        };
    }

    //calling the accommodation api
    componentDidMount(){
        this.retrieveAccommodations();
    }

    //display accommdation
    retrieveAccommodations(){
        axios.get('http://localhost:8070/accommodation/').then(res => {
            if(res.data.success){
                this.setState({
                    accommodations:res.data.existingAccommodations
                });
                console.log(this.state.accommodations)
            }
        });
    }


    //delete accomdation function
    onDelete=(id)=>{
        axios.delete(`http://localhost:8070/accommodation/delete/${id}`).then((res) => {
            alert("Accommdation Deleted");
            this.retrieveAccommodations();
        })
    }

    //search  accommdation function
    filterData(accommodations,searchKey){
        const result = accommodations.filter((accommodation) =>
            accommodation.accommodationType.toLowerCase().includes(searchKey)||
            accommodation.name.toLowerCase().includes(searchKey)||
            accommodation.noOfRomm.toLowerCase().includes(searchKey)||
            accommodation.mobile.toLowerCase().includes(searchKey)
        )
        this.setState({accommodations:result})
    }

    handleSearchArea=(e)=>{
        const searchKey=e.currentTarget.value.toLowerCase();
        axios.get('http://localhost:8070/accommodation/').then(res => {
            if(res.data.success){
                this.filterData(res.data.existingAccommodations,searchKey)
            }
        });
    }


    render() {
        return (
            <div className="container containerTop">
                <div className="row">
                    <div className="col-2"></div>
                    <div className="col-10">
                        <div className="row">
                            <div className="col position-relative link">
                                <p><a href="/">Accommodation Management</a> {'>'} Accommodations</p>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-9 position-relative">
                                <h2>Accommodation Details</h2>
                            </div>
                            <hr className="hr" style={{ height: '2px', color: '#0a90e8' }} />
                        </div>
                        <div className="shadowBox">
                            <div className="row">
                                <div className="col-4"></div>
                                <div className="col-4 search position-relative">
                                    <i className="fa fa-search"></i> <input className="form-control" type="Search" placeholder="Search a driver" name="searchQuery" onChange={this.handleSearchArea} />
                                </div>
                                <div className="col-4"></div>
                            </div>
                            <div className="row">
                                <div className="col-12 ">
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th scope="col">No</th>
                                                <th scope="col">Accommodation Type</th>
                                                <th scope="col">Name</th>
                                                <th scope="col">No of Room</th>
                                                <th scope="col">Telphone number</th>
                                                <th>Action</th>
                                            </tr>
                                        </thead>
                                        {this.state.accommodations.map((accommodations, index) => (
                                            <tbody>
                                                <tr>
                                                    <th scope="row"><a href={`/accommodation/${accommodations._id}`} style={{ textDecoration: 'none' }}>{index + 1}</a></th>
                                                    <td>{accommodations.accommodationType}</td>
                                                    <td>{accommodations.name}</td>
                                                    <td>{accommodations.noOfRomm}</td>
                                                    <td>{accommodations.mobile}</td>
                                                    <td>
                                                        <a href="" type="button" class="btn btn-success">
                                                            <i class="far fa-edit"></i>&nbsp;Edit
                                                        </a>&nbsp;&nbsp;
                                                        <a href="#" type="button" class="btn btn-danger" onClick={() => this.onDelete(accommodations._id)}>
                                                            <i className="far fa-trash-alt"></i>&nbsp;Delete
                                                        </a>
                                                    </td>
                                                </tr>
                                            </tbody>
                                        ))}
                                    </table>
                                </div>
                            </div>
                            <div className="row">
                                <div className="col-9"></div>
                                <div className="col-3 addv">
                                    <a href="/add_driver" type="button" class="btn btn-lg" ><i class="fas fa-user-plus"></i>&nbsp;&nbsp;Create New Driver</a><br /><br />
                                    <a onClick="" to="#" className="btn report"><i class="fas fa-download"></i>&nbsp;&nbsp;Download Report</a>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
