import axios from 'axios';
import React, { Component } from 'react'
import { ToastContainer, toast } from "react-toastify";

export default class AddAccommodation extends Component {

    constructor(props){
        super(props);
        this.state={
            accommodationType:"",
            name:"",
            noOfRomm:Number,
            mobile:Number
        }
    }

    handleTypeChange=(e)=>{
        this.setState({ accommodationType: e.target.value });
    }

    handleNameChange=(e)=>{
        this.setState({ name: e.target.value });
    }

    onSubmit=(e)=>{
        e.preventDefault();
        const {accommodationType,name,noOfRomm,mobile}=this.state;
        const data={
            accommodationType:accommodationType,
            name:name,
            noOfRomm:noOfRomm,
            mobile:mobile
        }

        axios.post("http://localhost:8070/accommodation/add",data).then((res)=>{
            if(res.data.success){
                toast.success("New Driver Added");
                this.setState(
                    {
                        accommodationType:"",
                        name:"",
                        noOfRomm:Number,
                        mobile:Number                        
                    }
                )
            }else{
                toast.error("You have an Error in Inserting");
            }
        })
    }

    render() {
        return (
            <div>
                <form>
                    <input type="text" placeholder="enter accommodation name" onChange={this.handleNameChange} value={this.state.name} />
                </form>
            </div>
        )
    }
}
